mapper.select:
SELECT count(*) FROM user_tb
WHERE 1=1 
	AND user_email = #{userEmail} 
	AND user_pw = #{userPw}

SELECT user_no,user_nick FROM user_tb
WHERE 1=1 
	AND user_email = #{userEmail} 
	AND user_pw = #{userPw}

SELECT count(*) FROM user_tb
WHERE 1=1
	AND user_email = #{userEmail} 
	AND user_phone = #{userPhone}

SELECT * FROM user_tb
WHERE 1=1
	AND user_email = #{userEmail} 
	AND user_phone = #{userPhone}

SELECT user_Email FROM user_tb
WHERE 1=1
	AND user_Name = #{userName}
	AND user_Phone = #{userPhone}

mapper.update:
UPDATE user_tb
	SET user_Pw = #{userPw}
WHERE user_Email = #{userEmail}
